import React from 'react';
import '../page/Products/Products.css'
import 'bootstrap/dist/css/bootstrap.min.css';
 import { Dropdown, MenuItem, Button, ButtonToolbar, DropdownButton, SplitButton, Container, Row, Col, FormControl, Nav, thumbnail, Table, Image } from 'react-bootstrap';
 import Modal from "../../components/Model";
import ImageGallery from 'react-image-gallery';


export default class ProductsItem extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      modal: false,
      name: "",
      modalInputName: "",
      Products: [],
      Categories:[]
    };
  }

    async componentDidMount() {
//Products
    const response = await fetch('http://localhost:8080/Products/');
    const result = await response.json();
    console.log(result);




    }
  handleChange(e) { 
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    this.setState({ name: this.state.modalInputName });
    this.modalClose();
  }

  modalOpen() {
    this.setState({ modal: true });
  }

  modalClose() {
    this.setState({
      modalInputName: "",
      modal: false
    });
  }

  render() {

    return  (
    <>

        <Modal className="formm" show={this.state.modal} handleClose={e => this.modalClose(e)}>
         <br/>
          <div className="form-group ">
            <label>Enter Name:</label>
            <input
              type="text"
              value={this.state.modalInputName}
              name="modalInputName"
              onChange={e => this.handleChange(e)}
              className="form-control"
            />
            <label>Enter Phone:</label>
            <input
              type="text"
              value={this.state.modalInputName}
              name="modalInputName"
              onChange={e => this.handleChange(e)}
              className="form-control"
            />
            <label>Enter Date:</label>
            <input
              type="text"
              value={this.state.modalInputName}
              name="modalInputName"
              onChange={e => this.handleChange(e)}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <Button className="modal-close"  onClick={e => this.handleSubmit(e)}>
              Submit
            </Button>
          </div>
        </Modal>




    { this.state.Categories.map(Categorie =>

<h1 className='topicPro' >{Categorie.name}</h1>

)}


   <Container>
    <Row className="row">
         { this.state.Products.map(Product =>


                    <Col calssName="col cl" sm>

      <div className="flip-box">
        <div className="flip-box-inner">
          <div className="flip-box-front">
            <img className="ImagePr"  src={require("../../Image/image.jpg")} alt="Paris" />
          </div>
          <div className="flip-box-back">
            <h2 className='topicPro'>Akram</h2>
            <p  ><span>Name:</span> {Product.name}</p>
                 <p><span>Description:</span> {Product.description}</p>
                 <p><span>Price:</span> {Product.price} $</p>

    <Button className='add add2'  onClick={e => this.modalOpen(e)}  size="lg" variant="danger">Add new product</Button>

          </div>
        </div>
      </div>

                    </Col>




                    )
  }


          </Row>         


    </Container>


            </>
            )
  }
}
