import React, { Component } from 'react'
import { Modal, Button, Row, col, Form } from 'react-bootstrap';

export class AddEventForm extends Component {

    constructor(props) {
        super(props);
        this.state = {


            description: '',
            location: '',
            date: '',
            image:''

        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value

        });
    }

    async handleSubmit(event) {

        event.preventDefault()

        const responseO = await fetch('http://localhost:8080/Events/create/?date=${this.state.date}&description=${this.state.description}&Location=${this.state.Location}&${this.stat.eventImg}');
        const result = await responseO.json();
        window.location.reload();

    }

    render() {
        return (

            <Modal
                {...this.props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        <h3>  Fill the from To add a new Event</h3>
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="container">
                        <form onSubmit={this.handleSubmit}>



                            <Form.Group  >
                                <Form.Label>Name</Form.Label>
                                <Form.Control type="text" name="name" value={this.state.description}  onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group  >
                                <Form.Label>Phone</Form.Label>
                                <Form.Control name="phone" type="number" value={this.state.phone} onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group >
                                <Form.Label>Date</Form.Label>
                                <Form.Control name="date" type="date" value={this.state.date} onChange={this.handleChange} />
                            </Form.Group>


                            {/* <input type="date" name="date" value={this.state.date} onChange={this.handleChange} /> */}

                            <Button variant="danger" size="lg" block="block" type="submit">
                                Create Order
        </Button>
                        </form>

                        <form onSubmit={this.handleSubmit}><br />
                            <label >  Description:<textarea type="text" name="name"  value={this.state.description} onChange={this.handleChange}/></label><hr />
                            <label>  Location:<input value={this.state.location} name="name"  type="text" onChange={this.handleChange} /></label><hr />
                            <label>  Date:<input value={this.state.date} name="name"  type="date" onChange={this.handleChange} /></label>

                            <label>  Image:<input value={this.state.image} name="name"   type="text" onChange={this.handleChange}/></label>
                            <hr />
                            <input type="submit" value="Submit" />
                        </form>
                    </div>

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="danger" onClick={this.props.onHide}>Close</Button>
                </Modal.Footer>
            </Modal>

        );
    }
}
