import React from 'react';
import '../page/Products/Products.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown, MenuItem, Form, Button, ButtonToolbar, DropdownButton, SplitButton, Container, Row, Col, FormControl, Nav, thumbnail, Table, Image } from 'react-bootstrap';
import Modal from "./Model";
import ImageGallery from 'react-image-gallery';



export default class CategoryItems extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      products: [],

      catID: this.props.value
    }

  }

  async componentDidMount() {

    //Products
    const response = await fetch(`http://localhost:8080/OrderByCatProducts/${this.state.catID}`);
    const result = await response.json();
    //Order
    // const responseO = await fetch(`http://localhost:8080/Orders/create/${this.state.date}'&'${this.state.username}'&'${this.state.userPhone}&'${this.state.catID}`);
    // const resultO = await responseO.json();


    this.setState({
      products: result, error: "none",

      modal: false,
      name: "",
      modalInputName: ""
    })

  }


  handleChange(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    this.setState({ name: this.state.modalInputName });
    this.modalClose();
  }

  modalOpen() {
    this.setState({ modal: true });
  }

  modalClose() {
    this.setState({
      modalInputName: "",
      modal: false
    });
  }



  render() {
    return (
      <>
        <Modal show={this.state.modal} handleClose={e => this.modalClose(e)}>

          <form onSubmit={this.handleSubmit}>
            <label htmlFor="username">Enter username</label>
            <input id="username" name="username" type="text" />

            <label htmlFor="email">Enter your email</label>
            <input id="email" name="email" type="email" />

            <label htmlFor="birthdate">Enter your birth date</label>
            <input id="birthdate" name="birthdate" type="text" />

            <button>Send data!</button>
          </form> </Modal>
        {
          this.state.products.map((product, index) => (
            <Col calssName="col cl" sm>
              <div className="flip-box">
                <div className="flip-box-inner">
                  <div className="flip-box-front">
                    <img className="ImagePr" src={`/Image/${product.proImg}`} alt="Paris" />
                  </div>
                  <div className="flip-box-back">
                    <h2 className='topicPro'>Akram</h2>
                    <p><span>Name:</span> {product.name}</p>
                    <p><span>Description:</span> {product.description}</p>
                    <p><span>Price:</span> {product.price} $</p>
                    {/* <p><span>Price:</span> {props.value.Categories_ID} $</p> */}
                    <Button className='add add2' size="lg" onClick={e => this.modalOpen(e)} variant="danger">Add new product</Button>
                  </div>
                </div>
              </div>

            </Col>

          ))
        }
      </>
    )
  }
}
