import React,{useState, useEffect} from 'react';
import './product.css'
import 'bootstrap/dist/css/bootstrap.min.css';

import {Dropdown,Button,ButtonToolbar,DropdownButton,SplitButton ,Container,Row,Col,FormControl,Nav,thumbnail,Table,Image} from 'react-bootstrap';




class product extends React.Component {



  render() {

    return (
        <>
        <Button className='add' size="lg" variant="danger">Add new product</Button> 
        <Container>

  <Row>
    <Col sm={2}>
        <img
    width={170}
    height={170}
    className="mr-3"
    src={require("../Image/image.jpg")}
        alt="Generic placeholder"

  />
  </Col>
    <Col sm={8}>
  <p><span>Name:</span> cake</p>
    <p><span>Description:</span> bla bla</p>
    <p><span>Price:</span> 10 $</p>
    <span>Categories:</span>  <ButtonToolbar>
    {[DropdownButton, SplitButton].map((DropdownType, idx) => (
      <DropdownType
        size="sm"
        variant="secondary"
        title="Drop small"
        id={`dropdown-button-drop-${idx}`}
        key={idx}
      >
        <Dropdown.Item eventKey="1">Action</Dropdown.Item>
        <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
        <Dropdown.Item eventKey="3">Something else here</Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
      </DropdownType>
    ))}
  </ButtonToolbar>
    </Col>
    <Col sm>sm=true</Col>
  </Row>
  </Container>
      </>
    );
  }
}

export default product;
