import React from 'react';
import '../page/Products/Products.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown, MenuItem, Form, Button, ButtonToolbar, DropdownButton, SplitButton, Container, Row, Col, FormControl, Nav, thumbnail, Table, Image } from 'react-bootstrap';
import Modal from "./Model";
import ImageGallery from 'react-image-gallery';



export default class CategoryItems extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      products: [],
      name: '',
      phone: '',
      date: '',
      catID: this.props.value
    }
    this.onSubmit = this.onSubmit.bind(this);
  }
  onChangeName(e) {
    this.setState({ name: e.target.value })
  }

  onChangePhone(e) {
    this.setState({ phone: e.target.value })
  }

  onChangeDate(e) {
    this.setState({ date: e.target.value })
  }

  onSubmit(e) {
    e.preventDefault()

    console.log(`Student successfully created!`);
    console.log(`Name: ${this.state.name}`);
    console.log(`Email: ${this.state.phone}`);
    console.log(`Roll no: ${this.state.date}`);

    this.setState({ name: '', phone: '', date: '' })
  }
  async componentDidMount() {

    //Products
    const response = await fetch(`http://localhost:8080/OrderByCatProducts/${this.state.catID}`);
    const result = await response.json();
    //Order
    // const responseO = await fetch(`http://localhost:8080/Orders/create/${this.state.date}'&'${this.state.username}'&'${this.state.userPhone}&'${this.state.catID}`);
    // const resultO = await responseO.json();


    this.setState({
      products: result, error: "none",

      modal: false,
      name: "",
      modalInputName: ""
    })

  }


  handleChange(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    this.setState({ name: this.state.modalInputName });
    this.modalClose();
  }

  modalOpen() {
    this.setState({ modal: true });
  }

  modalClose() {
    this.setState({
      modalInputName: "",
      modal: false
    });
  }



  render() {
    return (
      <>
        <Modal show={this.state.modal} handleClose={e => this.modalClose(e)}>

          <Form>
            <br />
            <Form.Group controlId="Name" >
              <Form.Label> Name</Form.Label>
              <Form.Control type="text" value={this.state.name} onChange={this.onChangeName} />
            </Form.Group>
            <Form.Group controlId="Phone" >
              <Form.Label>Phone</Form.Label>
              <Form.Control type="text" value={this.state.phone} onChange={this.onChangePhane} />
            </Form.Group>

            <Form.Group controlId="Date">
              <Form.Label>Date</Form.Label>
              <Form.Control type="date" value={this.state.Date} onChange={this.onChangeDate} />
            </Form.Group>


            <Button variant="danger" size="lg" block="block" type="submit">
              Create Order
        </Button>
          </Form>
        </Modal>
        {
          this.state.products.map((product, index) => (
            <Col calssName="col cl" sm>
              <div className="flip-box">
                <div className="flip-box-inner">
                  <div className="flip-box-front">
                    <img className="ImagePr" src={`/Image/${product.proImg}`} alt="Paris" />
                  </div>
                  <div className="flip-box-back">
                    <h2 className='topicPro'>Akram</h2>
                    <p><span>Name:</span> {product.name}</p>
                    <p><span>Description:</span> {product.description}</p>
                    <p><span>Price:</span> {product.price} $</p>
                    {/* <p><span>Price:</span> {props.value.Categories_ID} $</p> */}
                    <Button className='add add2' size="lg" onClick={e => this.modalOpen(e)} variant="danger">Add new product</Button>
                  </div>
                </div>
              </div>

            </Col>

          ))
        }
      </>
    )
  }
}
