import '../Products/Products.css'
import 'bootstrap/dist/css/bootstrap.min.css';
 import { Dropdown, MenuItem, Button, ButtonToolbar, DropdownButton, SplitButton, Container, Row, Col, FormControl, Nav, thumbnail, Table, Image } from 'react-bootstrap';
 import Modal from "../../components/Model";
import ImageGallery from 'react-image-gallery';
import ProductsItem from '../../components/ProdectsItem'

export default class Products extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      modal: false,
      name: "",
      modalInputName: "",
      Products: [],
      Categories:[]
    };
  }

    async componentDidMount() {
//Products
    const response = await fetch('http://localhost:8080/Products/');
    const result = await response.json();
    console.log(result);
    //Gategories
    const responseG = await fetch('http://localhost:8080/Categories/');
    const resultG = await responseG.json();
    console.log(resultG);
    //ProductsImage
    const responseI = await fetch('http://localhost:8080/ProductImages/');
    const resultI = await responseI.json();
    console.log(resultI);



      const responsep = await fetch(`http://localhost:8080/Categories/${Product.ID}`);
             const resultpI = await responsep.json();
                console.log(resultpI);



    this.setState({
           Products: result, error: "none",
           Categories: resultG,error:"none",
           CategoriesI:resultpI
           });


        }








  handleChange(e) { 
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    this.setState({ name: this.state.modalInputName });
    this.modalClose();
  }

  modalOpen() {
    this.setState({ modal: true });
  }

  modalClose() {
    this.setState({
      modalInputName: "",
      modal: false
    });
  }

  render() {

    return  (
    <>

      <Modal className="formm" show={this.state.modal} handleClose={e => this.modalClose(e)}>
        <br/>
        <div className="form-group ">
          <label>Enter Name:</label>
          <input
            type="text"
            value={this.state.modalInputName}
            name="modalInputName"
            onChange={e => this.handleChange(e)}
            className="form-control"
          />
          <label>Enter Phone:</label>
          <input
            type="text"
            value={this.state.modalInputName}
            name="modalInputName"
            onChange={e => this.handleChange(e)}
            className="form-control"
          />
          <label>Enter Date:</label>
          <input
            type="text"
            value={this.state.modalInputName}
            name="modalInputName"
            onChange={e => this.handleChange(e)}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <Button className="modal-close"  onClick={e => this.handleSubmit(e)}>
            Submit
          </Button>
        </div>
      </Modal>





 { this.state.Products.map(Product =>



   { this.state.CategoriesI.map(Categorie =>

    <h1 className='topicPro' >{Categorie.name}</h1>

)}
)}

   <Container>
    <Row className="row">
         { this.state.Products.map(Product =>


                    <Col calssName="col cl" sm>

      <div className="flip-box">
        <div className="flip-box-inner">
          <div className="flip-box-front">
            <img className="ImagePr"  src={require("../../Image/image.jpg")} alt="Paris" />
          </div>
          <div className="flip-box-back">
            <h2 className='topicPro'>Akram</h2>
            <p  ><span>Name:</span> {Product.name}</p>
                 <p><span>Description:</span> {Product.description}</p>
                 <p><span>Price:</span> {Product.price} $</p>

    <Button className='add add2'  onClick={e => this.modalOpen(e)}  size="lg" variant="danger">Add new product</Button>

          </div>
        </div>
      </div>

                    </Col>




                    )
  }


          </Row>         


    </Container>


            </>
            )
  }
}
