import React,{Component} from 'react';
import './App.css';
// import Nheader from './components/header'
// import Nproduct from './page/product'
// import order from './page/order'
class App extends Component {
  constructor(props){
    super(props);
    this.state = { Products : [] };

  }


  async componentDidMount(){

    const response =  await fetch ('http://localhost:8080/Products/');

    const result = await response.json();
    console.log(result);
    this.setState({Products:result, error:"none"});
  }
  render() {
    return (
      <>
       <div className='App'>
      Contact List:
      <ul>
      {
  this.state.Products.map(Product=><li>{Product.name} {Products.description}</li>)
      }
     </ul>
      </div>
      {/* <Nheader/>
      <Nproduct/> */}

      </>
    );
  }
}
export default App;
