import React, { Component } from 'react'
import { Modal, Button, Row, col, Form } from 'react-bootstrap';

export class AddEventForm extends Component {

    constructor(props) {
        super(props);
        this.state = {


            description: '',
            location: '',
            date: '',
            image: ''

        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);


    }
    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value

        });
    }

    async handleSubmit(event) {
        event.preventDefault()

         const responseO = await fetch(`http://localhost:8080/Events/create/?description=${this.state.description}&location=${this.state.location}&date=${this.state.date}&eventImg=evs`);
         const result = await responseO.json();
        window.location.reload();
     }

    render() {
        return (

            <Modal

            >




                     <form onSubmit={this.handleSubmit}>


                            <Form.Group  >
                                <Form.Label>description</Form.Label>
                                <Form.Control type="text" name="description" value={this.state.description} onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group  >
                                <Form.Label>location</Form.Label>
                                <Form.Control value={this.state.location} name="location" type="text" onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group >
                                <Form.Label>Date</Form.Label>
                                <Form.Control name="date" type="date" value={this.state.date} onChange={this.handleChange} />
                            </Form.Group>
                            {/* <Form.Group >
                                <Form.Label>Date</Form.Label>
                                <Form.Control name="image" type="file" value={this.state.image} onChange={this.handleChange} />
                            </Form.Group> */}


                            <Button variant="danger" size="lg" block="block" type="submit">
                                Create Events
        </Button>
                        </form>




            </Modal>

        );
    }
}
