import React from 'react';
import './productA.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container, Row, Col, Card } from 'react-bootstrap';
import { MdModeEdit } from 'react-icons/md';
export default class AdminProduct extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            Products: []
        };

    }
    async componentDidMount() {
        const response1 = await fetch('http://localhost:8080/Products/');
        const result = await response1.json();
        console.log(result);
        this.setState({
            Products: result
        })
    }
    render() {
        return (
            <>
                <Row>
                    {this.state.Products.map((product, index) =>
                        <Col style={{ width: '50%' }}>
                            <Card style={{ width: '20rem' }, { margin: '20px' }}>
                                <Card.Img variant="top" src={`/Image/${product.proImg}`} />
                                <Card.Body>
                                    <Card.Title>{product.name}</Card.Title>
                                    <Card.Text>{product.description}</Card.Text>
                                    <Button variant="info">
                                        width={40}
                                        height={40}
                                        ><MdModeEdit />
                                    </Button>
                                    <Button className="operation" variant="danger">

                                        <FaThumbsUp />
                                    </Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    )
                    }

                </Row>


            </>
        );
    }
}
