      Products: result, error: "none",
      Categories: resultG, error: "none",
      Cate: resulta, error: "none",
      Images: resultI, error: "none"
    });


  }

  handleChange(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    this.setState({ name: this.state.modalInputName });
    this.modalClose();
  }

  modalOpen() {
    this.setState({ modal: true });
  }

  modalClose() {
    this.setState({
      modalInputName: "",
      modal: false
    });
  }

  render() {

    return (
      <>

        <Modal className="formm" show={this.state.modal} handleClose={e => this.modalClose(e)}>
          <br />
          <div className="form-group ">
            <label>Enter Name:</label>
            <input
              type="text"
              value={this.state.modalInputName}
              name="modalInputName"
              onChange={e => this.handleChange(e)}
              className="form-control"
            />
            <label>Enter Phone:</label>
            <input
              type="text"
              value={this.state.modalInputName}
              name="modalInputName"
              onChange={e => this.handleChange(e)}
              className="form-control"
            />
            <label>Enter Date:</label>
            <input
              type="text"
              value={this.state.modalInputName}
              name="modalInputName"
              onChange={e => this.handleChange(e)}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <Button className="modal-close" onClick={e => this.handleSubmit(e)}>
              Submit
          </Button>
          </div>
        </Modal>



        <Container>
          {this.state.Images.map((image) => (
            <p>{image.path}</p>
          ))}
          {this.state.Cate.map(Categorie => (
            <div>
              <h1 className="topicPro">{Categorie.name}</h1>

              <Row>

                {this.state.Categories.map((cat, index) => (

                  < CategoryItems
                    value={cat}
                    key={index}
                  />
                )
                )
                }

              </Row>
            </div>
          )

          )}

        </Container>






        {/* 

        <Container>

          {this.state.Categories.map(Categorie => (
            <div>
              <h1 className="topicPro">{Categorie.name}</h1>

              <Row>
                {this.state.Products.map(product => (

                  <ProductsItem value={product} />
                )
                )
                }
              </Row>
            </div>
          )

          )}
        </Container> */}









      </>
    )
  }
}
