import React from 'react';
import './productA.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container, Row, Col, Card } from 'react-bootstrap';

export default class AdminProduct extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            Products: []
        };

    }
    async componentDidMount() {
        const response1 = await fetch('http://localhost:8080/Products/');
        const result = await response1.json();
        console.log(result);
        this.setState({
            Products: result
        })
    }
    render() {
        return (
            <>
                <Row>
                    {this.state.Products.map((product, index) =>
                        <Col style={{ width: '50%' }}>
                            <Card style={{ width: '20rem' }, {margin: '20px'}}>
                                <Card.Img variant="top" src={`/Image/${product.proImg}`} />
                                <Card.Body>
                                    <Card.Title>{product.name}</Card.Title>
                                    <Card.Text>{product.description}</Card.Text>
                                    <Button variant="warning"><svg
                                        onClick={this.del = async () => {
                                            window.confirm('Are you sure you wish to delete this item?'); 
                                            //window.location.reload(); const responsep = await fetch(`http://localhost:8080/Orders/delete/${Order.ID}`); const resultp = await responsep.json(); console.log(resultp);
                                        }} className="icon-trash"
                                        xmlns="http://www.w3.org/2000/svg"
                                        viewBox="0 0 28 40"
                                        width={40}
                                        height={40}
                                        >
                                        <path className="trash-lid" fillRule="evenodd" d="M6 15l4 0 0-3 8 0 0 3 4 0 0 2 -16 0zM12 14l4 0 0 1 -4 0z" /> <path className="trash-can" d="M8 17h2v9h8v-9h2v9a2 2 0 0 1-2 2h-8a2 2 0 0 1-2-2z" /></svg></Button>
                                        <Button className="operation" variant="danger"><svg
                                        onClick={this.del = async () => {
                                            window.confirm('Are you sure you wish to delete this item?'); 
                                            //window.location.reload(); const responsep = await fetch(`http://localhost:8080/Orders/delete/${Order.ID}`); const resultp = await responsep.json(); console.log(resultp);

                                        }} className="icon-trash"
                                        xmlns="http://www.w3.org/2000/svg"
                                        viewBox="0 0 28 40"
                                        width={40}
                                        height={40}
                                        >
                                        <path className="trash-lid" fillRule="evenodd" d="M6 15l4 0 0-3 8 0 0 3 4 0 0 2 -16 0zM12 14l4 0 0 1 -4 0z" /> <path className="trash-can" d="M8 17h2v9h8v-9h2v9a2 2 0 0 1-2 2h-8a2 2 0 0 1-2-2z" /></svg></Button>

                                </Card.Body>
                            </Card>
                        </Col>
                    )
                    }

                </Row>


            </>
        );
    }
}
