import React from 'react';
import '../Products/Products.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import PropTypes from 'prop-types';
import * as ReGal from '@hustlelikeaboss/react-gallery';
import unsplash from '@hustlelikeaboss/react-gallery'


import {Container,Row,Col,Image} from 'react-bootstrap';

 class Example extends React.Component{
	constructor(props) {
		super(props);
		this.state = { photos: [], numOfCols: 4 };
	}

	componentDidMount() {
		const collections = [239835, 1248080, 8052237];
		const page = 1;
		const photosPerPage = 15; 
		unsplash.collections
			.getCollectionPhotos(collections[0], page, photosPerPage)
			.then(res => res.json())
			.then(json => {
				const photos = json.map((photo) => ({
					id: photo.id,
					description: photo.description,
					url: photo.urls.regular,
					width: photo.width,
					height: photo.height
				}));
				this.setState({ photos });
			});
	}

	render() {
		const { photos, numOfCols } = this.state;
		return (
			<div>
				<h2>Preview</h2>
				<h3>Square, Masonry, MasonryAlt</h3>
				<ReGal.Masonry items={photos} numOfCols={numOfCols} />
			</div>
		);
	}
}
export default Example;
